    @Override
    public Action getAction() {
        return (final ActionContext actionContext) -> {
            try {
                final BetfairClient<BetfairClientParameters> client = actionContext.getClient();
                if (client != null) {
                    final JobDataMap jobDataMap = actionContext.getContext().getJobDetail().getJobDataMap();
                    final String locale = jobDataMap.getString(KEY_BETFAIR_LOCALE);
                    int fromRecord = 0;
                    int recordCount = 999999999;
                    final TimeRange itemDateRange = createTimeRange(jobDataMap);
                    final IncludeItem includeItem = IncludeItem.valueOf(jobDataMap.getString(KEY_BETFAIR_STATEMENT_INCLUDE_ITEM));
                    final Wallet wallet = Wallet.valueOf(jobDataMap.getString(KEY_BETFAIR_STATEMENT_WALLET));
                    boolean hasMore = true;
                    while (hasMore) {
                        final AccountStatementReport accountStatementReport = client.getAccountStatement(locale, fromRecord, recordCount, itemDateRange, includeItem, wallet);
                        hasMore = accountStatementReport.getMoreAvailable();
                        fromRecord = accountStatementReport.getAccountStatement().size();
                        Dispatcher.getInstance(actionContext.getServletContext(), actionContext.getScheduler()).dispatch(accountStatementReport, AccountStatementReport.class);
                    }
                } else {
                    LOG.log(Level.WARNING, MSG_BETFAIR_CLIENT_IS_NULL);
                }
            }
            catch (Throwable ex) {
                LOG.log(Level.SEVERE, null, ex);
            }
        };
    }

    private TimeRange createTimeRange(final JobDataMap jobDataMap) {
        Date fromDate = new Date();
        fromDate.setTime(fromDate.getTime() - jobDataMap.getLongFromString(KEY_BETFAIR_STATEMENT_RANGE_IN_HOURS) * MILLISECONDS_PER_HOUR);
        Date toDate = new Date();
        TimeRange itemDateRange = new TimeRange(fromDate, toDate);
        return itemDateRange;
    }

